def get_folder_files_bytes(dir_path):
    folder_count = 0
    file_count = 0
    total_size = 0
    for root, dirs, files in os.walk(dir_path):
        folder_count += len(dirs)
        file_count += len(files)
        for file in files:
            file_path = os.path.join(root, file)
            total_size += os.path.getsize(file_path)

